import React, { useState } from "react";

import { Legenda } from "../../components/legenda";

import { Container, Form, Add, Tabela, H2 } from "./styles";

import { Dia, Dep, Valor } from "../../global/styles";

import { formata } from "../../utils/helper";

export default function Donativos() {
  const mesArray = [
    "Janeiro",
    "Fevereiro",
    "Março",
    "Abril",
    "Maio",
    "Junho",
    "Julho",
    "Agosto",
    "Setembro",
    "Outubro",
    "Novembro",
    "Dezembro"
  ];
  const data = new Date();
  const [ano, setAno] = useState(data.getFullYear());
  const [mes, setMes] = useState("");
  const [dia, setDia] = useState("");
  const [deposito, setDeposito] = useState("");
  const [om, setOm] = useState("");
  const [cong, setCong] = useState("");
  const [reforma, setReforma] = useState("");
  const [lancamento, setLancamento] = useState([]);
  const [periodo, setPeriodo] = useState([]);
  //const periodo = [{ mes_ano : '2020', {}}]

  // useEffect(() => {}, [lancamento]);

  const totalOm = lancamento.reduce(
    (a, b) => a + parseFloat(b.om.replace(",", ".")),
    0
  );

  const totalCong = lancamento.reduce(
    (a, b) => a + parseFloat(b.cong.replace(",", ".")),
    0
  );

  const totalReforma = lancamento.reduce(
    (a, b) => a + parseFloat(b.reforma.replace(",", ".")),
    0
  );

  const totalDeposito = lancamento
    .map(i => parseFloat(i.om) + parseFloat(i.cong) + parseFloat(i.reforma))
    .reduce((a, b) => a + b, 0);

  return (
    <>
      <Legenda
        ano={ano}
        mesLista={mesArray}
        mesAtual={mes}
        mudaMes={e => setMes(e.target.value)}
        mudaAno={e => setAno(e.target.value)}
      />

      <Form>
        <Dia
          id="dia"
          type="date"
          value={dia}
          onChange={e => setDia(e.target.value)}
          placeholder="Dia"
        />
        <Dep
          value={deposito}
          type="number"
          onChange={e => setDeposito(e.target.value)}
          placeholder="Depositado"
        />
        <Valor
          value={om}
          type="number"
          onChange={e => setOm(e.target.value)}
          placeholder="Obra Mundial"
        />
        <Valor
          value={cong}
          type="number"
          onChange={e => setCong(e.target.value)}
          placeholder="Congregação"
        />
        <Valor
          value={reforma}
          type="number"
          onChange={e => {
            setReforma(e.target.value);
          }}
          placeholder="Reforma Betel"
        />
        <Add
          onClick={e => {
            e.preventDefault();
            if (dia && deposito && om && cong && reforma) {
              setLancamento([
                ...lancamento,
                { dia, deposito, om, cong, reforma }
              ]);
              setDia("");
              setDeposito("");
              setOm("");
              setCong("");
              setReforma("");
              document.getElementById("dia").focus();
            } else {
              alert("preencha todos os campos");
            }
          }}
        >
          Adicionar
        </Add>
      </Form>
      <Container>
        <Tabela>
          <thead>
            <tr key={"cabecalho"}>
              <th>Dia</th>
              <th>Depositado</th>
              <th>Obra Mundial</th>
              <th>Congregação</th>
              <th>Reforma Betel</th>
              <th>Depósito</th>
            </tr>
          </thead>
          <tbody>
            {lancamento.map((i, id) => (
              <tr key={id}>
                <td>{i.dia}</td>
                <td>{i.deposito}</td>
                <td>{formata(parseFloat(i.om))}</td>
                <td>{formata(parseFloat(i.cong))}</td>
                <td>{formata(parseFloat(i.reforma))}</td>
                <td>
                  {formata(
                    parseFloat(i.om.replace(",", ".")) +
                      parseFloat(i.cong.replace(",", ".")) +
                      parseFloat(i.reforma.replace(",", "."))
                  )}
                </td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td colSpan="2">Total</td>
              <td>
                {/* OM */}
                {formata(totalOm)}
              </td>
              <td>
                {/* congregação */}
                {formata(totalCong)}
              </td>
              <td>
                {/* reforma betel */}
                {formata(totalReforma)}
              </td>
              <td>
                {/* depositos */}
                {formata(totalDeposito)}
              </td>
            </tr>
          </tfoot>
        </Tabela>
        <H2>Enviar {formata(totalOm + totalReforma)} para Betel</H2>
        <Add
          onClick={e => {
            e.preventDefault();
            if (ano && mes) {
              setPeriodo([
                ...periodo,
                {
                  ano,
                  mes,
                  lancamento
                }
              ]);
            } else {
              alert("Ano e mês devem estar preenchido");
            }
            console.log(periodo);
          }}
        >
          Salvar
        </Add>
      </Container>
    </>
  );
}
